// import components
import Head from 'next/head';
import Layout from '../components/Layout';
import HeroImage from '../components/HeroImage';
import Header from '../components/Header';
import HamburguerButton from '../components/HamburguerButton';
import ScrollTop from '../components/ScrollTop';
import { SobreMi } from '../components/SobreMi';
import { ProyectsHome } from '../components/ProyectsHome';
import Footer from '../components/Footer';
import '../scss/mediaqueries.scss';

const Index = (props) => {
  return (
    <Layout>
      <Head>
        <title>Porfolio Dami√°n Vigo</title>
      </Head>
      <HeroImage />
      <Header />
      <HamburguerButton />
      <ScrollTop />
      <SobreMi />
      <ProyectsHome />
      <Footer />
    </Layout>
  );
};

Index.getInitialProps = () => {
 

  function sticky(header) {
    const $head = d.querySelector(header);

    w.addEventListener('scroll', (e) => {
      $head.classList.toggle('sticky', w.scrollY > 0);
    });
  }

  sticky('.header');
  // hamburguerMenu('.panel-btn', '.panel', '.panel__menu-a');
  // scrollTopButton('.scroll-top-btn');
  // darkMode('#switch');
};

export default Index;
